interface GuelmimMapProps {
  selectedProvince: string | null;
  onSelectProvince: (id: string | null) => void;
  activeProvinces: string[];
  language?: string;
  variant?: "sidebar" | "full";
}

const GuelmimMap: React.FC<GuelmimMapProps> = ({
  selectedProvince,
  onSelectProvince,
  activeProvinces,
  language = "ar",
  variant = "sidebar",
}) => {
  const [hovered, setHovered] = useState<string | null>(null);
  const isRtl = language === "ar";
  const isFull = variant === "full";

  const handleClick = (id: string) => {
    if (!activeProvinces.includes(id)) return;
    onSelectProvince(selectedProvince === id ? null : id);
  };

  const getName = (p: Province) => {
    if (language === "ar") return p.nameAr;
    if (language === "fr") return p.nameFr;
    return p.nameEn;
  };

  const getProducts = (p: Province) =>
    language === "ar" ? p.products.ar : p.products.en;

  const LabelItem = ({
    province,
    side,
  }: {
    province: Province;
    side: "left" | "right";
  }) => {
    const isActive = activeProvinces.includes(province.id);
    const isSelected = selectedProvince === province.id;
    const isHov = hovered === province.id;

    return (
      <div
        onClick={() => handleClick(province.id)}
        onMouseEnter={() => setHovered(province.id)}
        onMouseLeave={() => setHovered(null)}
        style={{
          display: "flex",
          alignItems: "center",
          gap: isFull ? "12px" : "8px",
          padding: isFull ? "12px 16px" : "9px 12px",
          borderRadius: "12px",
          cursor: isActive ? "pointer" : "default",
          opacity: isActive ? 1 : 0.35,
          background: isSelected
            ? `${province.fillHover}`
            : isHov && isActive
            ? `${province.fillIdle}`
            : "transparent",
          border: isSelected
            ? `1.5px solid ${province.color}`
            : isHov && isActive
            ? `1.5px solid ${province.color}55`
            : "1.5px solid transparent",
          transition: "all 0.2s ease",
          direction: isRtl ? "rtl" : "ltr",
          flexDirection: side === "right" && !isRtl ? "row-reverse" : "row",
        }}
      >
        {/* Colored accent dot */}
        <div
          style={{
            width: isFull ? "9px" : "7px",
            height: isFull ? "9px" : "7px",
            borderRadius: "50%",
            flexShrink: 0,
            background: isSelected
              ? province.color
              : isActive
              ? `${province.color}99`
              : "#ccc",
            transition: "all 0.2s",
            boxShadow: isSelected
              ? `0 0 0 3px ${province.color}28`
              : "none",
          }}
        />
        <div style={{ flex: 1 }}>
          <div
            style={{
              fontWeight: 600,
              fontSize: isFull ? "14px" : "12px",
              color: isSelected ? province.color : "#3D405B",
              marginBottom: "2px",
              fontFamily:
                language === "ar"
                  ? "'Noto Sans Arabic', sans-serif"
                  : "inherit",
              transition: "color 0.2s",
            }}
          >
            {getName(province)}
          </div>
          <div
            style={{
              fontSize: isFull ? "11px" : "10px",
              color: isSelected ? `${province.color}bb` : "#aaa",
              fontFamily:
                language === "ar"
                  ? "'Noto Sans Arabic', sans-serif"
                  : "inherit",
              whiteSpace: "nowrap",
              overflow: "hidden",
              textOverflow: "ellipsis",
              maxWidth: isFull ? "155px" : "115px",
              transition: "color 0.2s",
            }}
          >
            {getProducts(province)}
          </div>
        </div>
      </div>
    );
  };

  const MapSVG = () => (
    <svg
      viewBox="-5 47 446 310"
      xmlns="http://www.w3.org/2000/svg"
      style={{
        width: "100%",
        height: "auto",
        display: "block",
        filter: isFull
          ? "drop-shadow(0 6px 24px rgba(0,0,0,0.08))"
          : "none",
      }}
      aria-label="خريطة جهة كلميم واد نون"
    >
      {provinceList.map((province) => {
        const isActive = activeProvinces.includes(province.id);
        const isSelected = selectedProvince === province.id;
        const isHov = hovered === province.id;

        const fill = !isActive
          ? "#f0f0f0"
          : isSelected
          ? province.fillSelected
          : isHov
          ? province.fillHover
          : province.fillIdle;

        const stroke = !isActive
          ? "#ddd"
          : isSelected
          ? province.color
          : isHov
          ? province.color
          : `${province.color}88`;

        const strokeWidth = isSelected
          ? 2
          : isHov && isActive
          ? 1.8
          : 1.2;

        return (
          <g key={province.id}>
            <path
              d={province.path}
              fill={fill}
              stroke={stroke}
              strokeWidth={strokeWidth}
              strokeLinejoin="round"
              style={{
                cursor: isActive ? "pointer" : "default",
                transition: "fill 0.22s ease, stroke 0.15s ease, stroke-width 0.15s ease",
                filter: isSelected
                  ? `drop-shadow(0 2px 8px ${province.color}44)`
                  : isHov && isActive
                  ? `drop-shadow(0 1px 4px ${province.color}33)`
                  : "none",
              }}
              onClick={() => handleClick(province.id)}
              onMouseEnter={() => setHovered(province.id)}
              onMouseLeave={() => setHovered(null)}
            />
            {isActive && (
              <text
                x={province.lx}
                y={province.ly}
                textAnchor="middle"
                fill={isSelected ? "#fff" : province.color}
                fontSize={
                  isFull
                    ? province.id === "sidi-ifni" ? 8 : 10
                    : province.id === "sidi-ifni" ? 7 : 9
                }
                fontWeight="700"
                fontFamily="'Noto Sans Arabic', system-ui, sans-serif"
                pointerEvents="none"
                style={{ userSelect: "none", transition: "fill 0.2s" }}
              >
                {getName(province)}
              </text>
            )}
          </g>
        );
      })}
    </svg>
  );

  // ─── FULL variant ─────────────────────────────────────────────────────
  if (isFull) {
    const leftProvinces = provinceList.filter((p) =>
      ["guelmim", "tan-tan"].includes(p.id)
    );
    const rightProvinces = provinceList.filter((p) =>
      ["sidi-ifni", "assa-zag"].includes(p.id)
    );

    return (
      <div
        style={{
          width: "100%",
          maxWidth: "860px",
          margin: "0 auto",
          display: "flex",
          alignItems: "center",
          gap: "16px",
          direction: isRtl ? "rtl" : "ltr",
          padding: "0 8px",
        }}
      >
        <div style={{ display: "flex", flexDirection: "column", gap: "8px", flex: "0 0 180px" }}>
          {leftProvinces.map((p) => <div key={p.id}><LabelItem province={p} side="left" /></div>)}
        </div>
        <div style={{ flex: 1, minWidth: 0 }}>
          <MapSVG />
        </div>
        <div style={{ display: "flex", flexDirection: "column", gap: "8px", flex: "0 0 180px" }}>
          {rightProvinces.map((p) => <div key={p.id}><LabelItem province={p} side="right" /></div>)}
        </div>
      </div>
    );
  }

  // ─── SIDEBAR variant ──────────────────────────────────────────────────
  const leftProvinces = provinceList.filter((p) =>
    ["guelmim", "tan-tan"].includes(p.id)
  );
  const rightProvinces = provinceList.filter((p) =>
    ["sidi-ifni", "assa-zag"].includes(p.id)
  );

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        gap: "8px",
        width: "100%",
        maxWidth: "700px",
        direction: isRtl ? "rtl" : "ltr",
      }}
    >
      <div style={{ display: "flex", flexDirection: "column", gap: "6px", flex: "0 0 140px", minWidth: 0 }}>
        {leftProvinces.map((p) => <div key={p.id}><LabelItem province={p} side="left" /></div>)}
      </div>
      <div style={{ flex: 1, minWidth: 0 }}>
        <MapSVG />
      </div>
      <div style={{ display: "flex", flexDirection: "column", gap: "6px", flex: "0 0 140px", minWidth: 0 }}>
        {rightProvinces.map((p) => <div key={p.id}><LabelItem province={p} side="right" /></div>)}
      </div>
    </div>
  );
};

export default GuelmimMap;
export const provinces = provinceList;